mapper.insert:
INSERT INTO TOUR_IMG (
    IMG_SEQ, TOUR_SEQ, TOUR_DAY, PLACE_SEQ, TITLE,  CONTENTS, LIKE_CNT,
    REG_ID, REG_DT, CHG_ID, CHG_DT, IMG_URL
)
VALUES (
           COALESCE((SELECT MAX(A.IMG_SEQ) FROM TOUR_IMG A), 0) + 1, #{tourSeq}, #{tourDay}, #{placeSeq},
           #{title}, #{contents}, 0, #{userId}, NOW(), #{userId}, NOW(), #{imgURL}
       )

mapper.update:
UPDATE TOUR_PLACE
SET
    IMG_LAT = #{imgLat},
    IMG_LON = #{imgLon},
    IMG_URL = #{imgURL}

WHERE
    TOUR_SEQ = #{tourSeq}
AND
    TOUR_DAY = #{tourDay}
AND
    PLACE_SEQ = #{placeSeq}

mapper.select:
SELECT IMG_SEQ, TOUR_SEQ, TOUR_DAY, PLACE_SEQ, TITLE, CONTENTS, REG_ID, REG_DT, CHG_ID, CHG_DT, LIKE_CNT, IMG_URL
FROM  TOUR_IMG
WHERE
    TOUR_DAY = #{tourDay}
  AND
    TOUR_SEQ = #{tourSeq}
  AND
    PLACE_SEQ = #{placeSeq}

SELECT IMG_SEQ, TOUR_SEQ, TOUR_DAY, PLACE_SEQ, TITLE, CONTENTS, REG_ID, REG_DT, CHG_ID, CHG_DT, LIKE_CNT, IMG_URL
FROM  TOUR_IMG
WHERE
    TOUR_DAY = #{tourDay}
  AND
    TOUR_SEQ = #{tourSeq}

mapper.delete:
DELETE FROM TOUR_IMG
WHERE
    TOUR_SEQ = #{tourSeq}
  AND
    TOUR_DAY = #{tourDay}
  AND
    PLACE_SEQ = #{placeSeq}

DELETE FROM TOUR_IMG
WHERE
    TOUR_SEQ = #{tourSeq}
  AND
    TOUR_DAY = #{tourDay}
