mapper.insert:
INSERT INTO MY_ADDR (
    USER_ID, SEQ, ADDR, MEMO
)
VALUES (
           #{userId},
           ((SELECT IFNULL(MAX(A.SEQ), 0) + 1 FROM MY_ADDR A WHERE A.USER_ID = #{userId}))
           ,
           #{addr}, #{memo}
       )

mapper.select:
SELECT USER_ID, SEQ, ADDR, MEMO
FROM MY_ADDR
WHERE
    USER_ID = #{userId}
AND
    SEQ = #{seq}

mapper.update:
UPDATE MY_ADDR
SET
    ADDR = #{addr},
    MEMO = #{memo}
WHERE
    USER_ID = #{userId}
AND
    SEQ = #{seq}

mapper.delete:
DELETE FROM MY_ADDR
WHERE
    USER_ID = #{userId}
AND
    SEQ = #{seq}
