mapper.select:
SELECT TOUR_SEQ, TOUR_NAME, TOUR_PROCESS
FROM TOUR_INFO
WHERE USER_ID = #{userId}
AND
    TOUR_NAME = #{tourName}

SELECT TOUR_SEQ, TOUR_NAME, TOUR_PROCESS, TOUR_END, USER_ID
FROM  TOUR_INFO
WHERE
    USER_ID = #{userId}
    AND
    TOUR_SEQ = #{tourSeq}

mapper.update:
UPDATE TOUR_INFO
SET
    TOUR_NAME = #{tourName}
WHERE
    TOUR_SEQ = #{tourSeq}
    AND
    USER_ID = #{userId}

UPDATE TOUR_INFO
SET
    TOUR_END = now(),
    TOUR_PROCESS = #{tourProcess}
WHERE
    TOUR_SEQ = #{tourSeq}
    AND
    USER_ID = #{userId}

UPDATE TOUR_INFO
SET
    TOUR_PROCESS = #{tourProcess}
WHERE
    TOUR_SEQ = #{tourSeq}
    AND
    USER_ID = #{userId}

mapper.delete:
DELETE FROM TOUR_INFO
WHERE
    TOUR_SEQ = #{tourSeq}
    AND
    USER_ID = #{userId}

DELETE FROM TOUR_DAY
WHERE TOUR_SEQ = #{tourSeq}
AND   TOUR_DAY = #{tourDay}

mapper.insert:
INSERT INTO TOUR_PLACE (
    TOUR_SEQ, TOUR_DAY, PLACE_SEQ, PLACE_NICK, PLACE_NAME, PLACE_ADDR, MEMO, LAT, LON
)
VALUES (
           #{tourSeq}, #{tourDay}, (SELECT COALESCE(MAX(A.PLACE_SEQ), 0) FROM TOUR_PLACE A WHERE A.TOUR_SEQ = #{tourSeq} AND A.TOUR_DAY = #{tourDay}) + 1,
           #{placeNick},#{placeName},#{placeAddr}, #{memo}, #{lat}, #{lon}
       )
